import React from "react";
import "./App.css";
import Header from "./Components/Header";
import Category, { CategoryProps } from "./Components/Category";

export const App = () => {
  const [categories, setCategories] = React.useState<CategoryProps[]>([
    {
      name: "Category 1",
      tasks: [{
        name: "Task 1",
        description: "Description 1",
        color: "#FF6700"
      }]
    },
    {
      name: "Category 2",
      tasks: [{
        name: "Task 1",
        description: "Description 1",
        color: "red"
      }]
    }
  ]);

  return (
    <div className="App">
      {/* Where the header will go */}
      <Header />

      {/* All task-related content */}
      <main className="taskManagerParent">
        {/* Where the tasks resides */}
        {/* Task catagory */}
        <div className="taskManagerChild">
          {/* Task */}

          {categories.map((category, index) => (
            <Category key={index} name={category.name} tasks={category.tasks} />
          ))}

        </div>
      </main>
    </div>
  );
};

export default App;
