import Task, { TaskProps } from "./Task";

export interface CategoryProps {
  name: string;
  tasks: TaskProps[];
}

const Category = (props: CategoryProps) => {
  return (
    <div className="category">
      <h1>{props.name}</h1>
      {props.tasks.map((task, index) => {
        return (
          <Task
            key={index}
            name={task.name}
            description={task.description}
          />
        );
      })}
    </div>
  );
};

export default Category;
