export interface TaskProps {
  name: string;
  description: string;
  /**
   * The hex code of the task color
   */
  color: string
}

const Task = (props: TaskProps) => {
  return (
    <div className="task">
      <h1>{props.name}</h1>
      {props.name}
      <br />
      {props.description}
    </div>
  );
};

export default Task;
