export interface TaskProps {
  name: string;
  description: string;
  /**
   * The hex code of the task color
   */
  color: string
}

const Task = (props: TaskProps) => {
  return (
    <div className="task">
      <div className=""></div>
      <h2 className="taskHeader">{props.name}</h2>
      <p className="taskDescription">{props.description}</p>
    </div>
  );
};

export default Task;
